var methods = {
    SUPREM: {
        encrypt: function encrypt(string, pass, iv, salt) {
            var sc = ""
            iv = parseInt(iv%65535);
            string = string.toString();
            pass = pass.toString();
            iv = parseInt(iv);
            salt = parseInt(salt);
        
            string.split('').forEach((val) => {
                var pssS = 0
                pass.split('').forEach(pss => {
                    pssS += pss.charCodeAt(0);
                });
        
                sc += `${(((val.charCodeAt(0) + iv) + pssS) + salt * salt * salt)} `;
            }); 

            sc = sc.substring(0, sc.length-1);
        
            return sc;
        },
        decrypt: function decrypt(string, pass, iv, salt) {
            var sd = ""
            iv = parseInt(iv%65535);
            string = string.toString();
            pass = pass.toString();
            iv = parseInt(iv);
            salt = parseInt(salt);
        
            string.split(' ').forEach((val) => {
                val = parseInt(val);
                var pssS = 0;
                pass.split('').forEach(pss => {
                    pssS += pss.charCodeAt(0);
                });
        
                sd += `${String.fromCharCode((((val - iv) - pssS) - salt * salt * salt))}`;
            }); 
        
            return sd;
        }
    }
};

module.exports = methods;